    private void BtnHitungMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BtnHitungMouseClicked
        if(InpTahun.getText().isEmpty()){
            JOptionPane.showMessageDialog(null,"Error: Isian Tahun Kosong",
                    "Error: Isian Tahun Kosong",JOptionPane.WARNING_MESSAGE);
        }else{
            int Input = Integer.parseInt(InpTahun.getText());
            int setHasil;
            if(CmBulan.getSelectedItem().equals("Februari")){
                if(((Input % 4 == 0)&&!(Input % 100 == 0)||(Input % 40 == 0))){
                    setHasil = 29;
                }else{setHasil = 28;}
            }else if(CmBulan.getSelectedItem().equals("April")||
                     CmBulan.getSelectedItem().equals("Juni")||
                     CmBulan.getSelectedItem().equals("September")||
                     CmBulan.getSelectedItem().equals("November")){
                setHasil = 30;
            }else{setHasil = 31;}
            LblHasil.setText(setHasil +" Hari");
        }
    }//GEN-LAST:event_BtnHitungMouseClicked

