    private void InpTahunKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_InpTahunKeyTyped
        char x = evt.getKeyChar();
            if(!((Character.isDigit(x)||(x == KeyEvent.VK_BACK_SPACE)||(x == KeyEvent.VK_DELETE)))){
                getToolkit().beep();
                JOptionPane.showMessageDialog(null,"Masukkan Hanya Angka");
                evt.consume();
            }
    }//GEN-LAST:event_InpTahunKeyTyped

    private void BtnHapusMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BtnHapusMouseClicked
        InpTahun.setText("");
        CmBulan.setSelectedItem("Januari");
        LblHasil.setText("0 Hari");
    }//GEN-LAST:event_BtnHapusMouseClicked

    private void BtnSimpanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BtnSimpanMouseClicked
        try{
            try(BufferedWriter output = new BufferedWriter(new FileWriter("Jumlah Hari"+InpTahun.getText()+CmBulan.getSelectedItem()+".txt"))){
                output.write(LblHasil.getText()+" Nama User");
                JOptionPane.showMessageDialog(null, "Berhasil di Simpan");
            }
        }catch(IOException|HeadlessException e){
                System.err.println("Err: "+e.getMessage());
            }
    }//GEN-LAST:event_BtnSimpanMouseClicked

